var calc_output = [];
var msg, mini_msg;
    var v = parseFloat(C) + parseFloat(I) + parseFloat(IC) + parseFloat(L) + parseFloat(M1) + parseFloat(M2) + parseFloat(M3) + parseFloat(M4) + parseFloat(M5) + parseFloat(M6);
    var u = 10 + v;
    if (u == 10) {mini_msg='Normal CT Scan'; msg = 'Normal CT Scan'; }
    if (u < 10 && u >= 8) {mini_msg='~40% mRS 0-1 at 90 days.'; msg = '~40% of patients with ASPECTS â‰¥8 will have mRS 0-1 at 90 days.'; }
    if (u < 8) {mini_msg='~27% mRS 0-1 at 90 days.'; msg = '~27% of patients with ASPECTS <8 will have mRS 0-1 at 90 days.'; }


calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3164_ASPECTS_result',
    value: u,
    value_text: 'points',
    message: msg
});
