data.logic:
var calc_output = [];

var u = parseFloat(def) + parseFloat(freq) + parseFloat(form);
var string, string2;
var mini_msg = 'For IBS'
if (u <= 1) {
    string = 'Negative';
    string2 = 'Unlikely IBS.';
}
if (u >= 2) {
    string = 'Positive';
    string2 = 'Likely IBS.';
}

calc_output.push({
    name: 'mini',
    value: string,
    value_text: ' ',
    message: mini_msg
});
calc_output.push({
    name: '3165_Rome III for IBS_result',
    value: string,
    value_text: ' ',
    message: string2
});
