data.logic:
var calc_output = [];

    var msg = '';
    var ecog;
    var u = parseFloat(karnofsky);
    if (u >= 10) { ecog = 'Correlates to an ECOG Performance Status of 4.<br/><br/>'; }
    if (u >= 30) { ecog = 'Correlates to an ECOG Performance Status of 3.<br/><br/>'; }
    if (u >= 50) { ecog = 'Correlates to an ECOG Performance Status of 2.<br/><br/>'; }
    if (u >= 70) { ecog = 'Correlates to an ECOG Performance Status of 1.<br/><br/>'; }
    if (u >= 90) { ecog = 'Correlates to an ECOG Performance Status of 0.<br/><br/>'; }
    if (u == 0) { ecog = 'Correlates to an ECOG Performance Status of 5.<br/><br/>'; }
    if (u <= 40) { mini_msg= 'Unable to care for self.'; msg = 'Unable to care for self; requires equivalent of institutional or hospital care; disease may be progressing rapidly.<br/><br/>'; }
    if (u > 40 && u <= 70) { mini_msg= 'Unable to work; able to live at home and care for most personal needs.'; msg = 'Unable to work; able to live at home and care for most personal needs; varying amount of assistance needed.<br/><br/>'; }
    if (u > 70) { mini_msg= 'Able to carry on normal activity and to work.'; msg = 'Able to carry on normal activity and to work; no special care needed.<br/><br/>'; }

    var addmsg = 'See definitions of the Karnofsky Scale on the About section.';

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3168_Karnofsky Scale_result',
    value: u,
    value_text: 'points',
    message: msg+ecog+addmsg
});
