data.logic:
var calc_output = [];

    var u;
    var msg = '';
    var mini_msg = '';
    u = parseFloat(zubrod);
    if (u == 0) { mini_msg='Fully active'; msg = 'Fully active, able to carry on all predisease activities without restriction.'; }
    if (u == 1) { mini_msg='Restricted in strenuous activity'; msg = 'Restricted in strenuous activity, ambulatory and able to do light work.'; }
    if (u == 2) { mini_msg='Capable of all self care'; msg = 'Capable of all self care, but no work activities, out of bed >50% of day.'; }
    if (u == 3) { mini_msg='Capable of limited self-care'; msg = 'Capable of limited self-care, confined to bed or chair >50% of day.'; }
    if (u == 4) { mini_msg='Completely disabled, no self-care'; msg = 'Completely disabled, no self-care, confined to bed or chair.'; }
    if (u == 5) { mini_msg='Dead'; msg = 'Dead.'; }
    var addmsg = 'See definitions of the ECOG/Zubrod Score in the About section.';

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3170_ECOG Performance Status_result',
    value: u,
    value_text: 'points',
    message: msg+'<br/><br/>'+addmsg
});
