data.logic:
var calc_output=[]; 

var u=parseFloat(confusion)+parseFloat(bun)+parseFloat(respiratory_rate)+parseFloat(systolic_diastolic_bp)+parseFloat(age_65);

var msg=''; 
var mini_msg='';

if(u==0){
    msg='Low risk group: 0.6% 30-day mortality.<br/><br/>Consider outpatient treatment.';
    mini_msg='Low Risk'
}
if(u==1){
    msg='Low risk group: 2.7% 30-day mortality.<br/><br/>Consider outpatient treatment.';
    mini_msg='Low Risk'
}
if(u==2){
    msg='Moderate risk group: 6.8% 30-day mortality.<br/><br/>Consider inpatient treatment or outpatient with close followup.';
    mini_msg='Moderate Risk'
}
if(u==3){
    msg='Severe risk group: 14.0% 30-day mortality.<br/><br/>Consider inpatient treatment with possible intensive care admission.';
    mini_msg='Severe Risk'
}
if(u>3){
    msg='Highest risk group: 27.8% 30-day mortality.<br/><br/>Consider inpatient treatment with possible intensive care admission.';
    mini_msg='Highest Risk'
} 

calc_output.push({
    name:'mini',
    value:u.toFixed(0),
    value_text:'points',
    message:mini_msg
}); 
calc_output.push({
    name:'324_CURB-65 Score_result',
    value:u.toFixed(0),
    value_text:'points',
    message:msg
});
