var calc_output = [];

var u = parseFloat(def) + parseFloat(freq) + parseFloat(form);
var string, string2, like;

if (u <= 1) {
    string = 'Negative ';
    like= 'Unlikely IBS';
    string2 = 'Patient unlikely suffers from IBS.<br/><br/>Consider further assessment for other pathology.';
}
if (u >= 2) {
    string = 'Positive ';
    like= 'Likely IBS';
    string2 = 'Patient likely suffers from IBS.';
}

calc_output.push({
    name: 'mini',
    value: string,
    value_text: ' ',
    message: like
});
calc_output.push({
    name: '3281_Rome II for IBS_result',
    value: string,
    value_text: ' ',
    message: string2
});
