data.logic:
var calc_output = [];

var u = parseFloat(peri) + parseFloat(creat) + parseFloat(hct);
var msg, mini_msg;

if (u == 0) { mini_msg='Non-severe pancreatitis'; msg='Non-severe pancreatitis.<br/><br/>A score of 0 suggests no pancreatic necrosis, need for dialysis, artificial ventilation, or fatal outcome.'; } 
else { mini_msg='Unable to rule out severe pancreatitis'; msg='Scores >0 do not rule out a harmful first attack of acute pancreatitis. Consider further testing and possible admission to the ICU.'; }

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3286_HAPS_result',
    value: u,
    value_text: 'points',
    message: msg
});
