data.logic:
var calc_output = [];

var u = parseInt(fev) + parseInt(cough) + parseInt(pur) + parseInt(symp) + parseInt(inf);
var msg, mini_msg;


if (u == 0) { mini_msg = '13-18% isolation of streptococcus.';
    msg = '<br/><br/>Unlikely streptococcal infection; antibiotics not recommended.'; }
if (u == 1) { mini_msg = '13-18% isolation of streptococcus.';
    msg = '<br/><br/>Unlikely streptococcal infection; antibiotics not recommended.'; }
if (u == 2) { mini_msg = '30-35% isolation of streptococcus.';
    msg = '<br/><br/>Consider delayed antibiotics.'; }
if (u == 3) { mini_msg = '39-48% isolation of streptococcus.';
    msg = '<br/><br/>Consider delayed antibiotics.'; }
if (u >= 4) { mini_msg = '62-65% isolation of streptococcus.';
    msg = '<br/><br/>Consider antibiotics.'; }

calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3316_FeverPAIN Score_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg + msg
});
