data.logic:
var calc_output = [];

var hypertension = parseFloat(hypertension)
var extension = parseFloat(extension)
var meld = parseFloat(meld)

var risk
var los
var mortality
var percent

if (hypertension == 1) {
   if (extension == 0) {
		risk = 'Intermediate'
     	los = 'Median length of stay 8 days'
     	mortality = '9.0'
     	percent = '28.6'
   }
	else if (extension == 1) {
		risk = 'High'
      	los = 'Median length of stay 11 days'
      	mortality = '25.0'
     	percent = '60.0'
   }
}
else if (hypertension == 0 && extension == 1){
risk = 'Intermediate'
los = 'Median length of stay 8 days'
mortality = '9.0'
percent = '28.6'
}

else if (hypertension == 0 && extension == 0) {
		if (meld == 0) {
		risk = 'Low'  
        los = 'Median length of stay 7 days'
        mortality = '4.4'
     	percent = '4.9'
   }
	else if (meld == 1) {
		risk = 'Intermediate'
      	los = 'Median length of stay 8 days'
      	mortality = '9.0'
     	percent = '28.6'
   }
}


//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: '',
    message:'Risk of liver decompensation'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: ''
});
calc_output.push({
    name: 'percent',
    value: percent,
    value_text: '%',
    message: 'Rate of postop liver decompensation <br/><br/>' + los
});
calc_output.push({
    name: 'mortality',
    value: mortality,
    value_text: '%',
    message: 'Risk of mortality due to liver failure'
});
