data.logic:
var calc_output = [];
var phenytoin = parseFloat(phenytoin);
var albumin = parseFloat(albumin);
var crcl_20 = parseFloat(crcl_20);
var units;
if (crcl_20 == 1) {
    var factor = 0.2;
} else {
    var factor = 0.275;
}

if (UOMSYSTEM == false) {
    //needed in US units, convert when in SI
    albumin = albumin / 10;
    phenytoin = phenytoin / 3.968;
}
var v = phenytoin / ((factor * albumin) + 0.1);
if (UOMSYSTEM == true) {
  units = '\u00b5g/mL';
}
if (UOMSYSTEM == false){
  v = v * 3.968;
  units = '\u00b5mol/L';
}
calc_output.push({ 
  name: 'mini', 
  value: v.toFixed(1), 
  value_text: units, 
  message: 'Corrected Phenytoin' 
});
calc_output.push({ 
  name: '333_Phenytoin Correction_result', 
  value: v.toFixed(1), 
  value_text: units, 
  message: 'Corrected Phenytoin Level' 
});
