data.logic:
var calc_output = [];

var wheezing = parseInt(wheezing);
var major = parseInt(asthma) + parseInt(eczema);
var minor = parseInt(rhin) + parseInt(cold) + parseInt(eos);
var msg;
var mini_msg;
var string;


if (wheezing == 1) { // Early wheezer but not early FREQUENT wheezer
    if (major > 0) {
        string = "Positive";
        mini_msg = "By ‘loose’ criteria";
        msg = "; 80% specific for later childhood asthma diagnosis.";
    } else if (major == 0 && minor <= 1) {
        string = "Negative";
        mini_msg = "By ‘loose’ criteria";
        msg = "; less likely to develop childhood asthma.";
    } else if (major == 0 && minor > 1) {
        string = "Positive";
        mini_msg = "By ‘loose’ criteria";
        msg = "; 80% specific for later childhood asthma diagnosis.";
    }
}
if (wheezing == 2) {
    if (major > 0) {
        string = "Positive";
        mini_msg = "By strict criteria";
        msg = "; >95% specific for later childhood asthma diagnosis.";
    } else if (major == 0 && minor <= 1) {
        string = "Negative";
        mini_msg = "By strict criteria";
        msg = "; less likely to develop childhood asthma.";
    } else if (major == 0 && minor > 1) {
        string = "Positive";
        mini_msg = "By strict criteria";
        msg = "; >95% specific for later childhood asthma diagnosis.";
    }
}

calc_output.push({ name: 'mini', value: string, value_text: ' ', message: mini_msg });
calc_output.push({ name: '3381_Asthma Predictive Index_result', value: string, value_text: ' ', message: mini_msg + msg });
