data.logic:
var calc_output = [];

areas = parseFloat(areas)
anemia = parseFloat(anemia)
thrombocytopenia = parseFloat(thrombocytopenia)

var binet
var risk
var survival

if (thrombocytopenia + anemia == 0) {
  	if (areas == 0){
	binet = 'A'
  	risk = 'Low'
  	survival = '12'
    }
  	else {
	binet = 'B'
  	risk = 'Intermediate'
  	survival = '7'
	}
}
else {
  	binet = 'C'
  	risk = 'High'
	survival = '2-4'
}

//results
calc_output.push({
    name: 'mini',
    value: 'Stage ' + binet,
    value_text: '',
    message: survival + ' years overall survival'
});
calc_output.push({
    name: 'classification',
    value: 'Stage ' + binet,
    value_text: '',
    message: 'Binet Stage'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: ''
});
calc_output.push({
    name: 'survival',
    value: survival,
    value_text: 'years',
    message: 'Overall survival'
});
