data.logic:
var calc_output = [];
var u = parseInt(age_60) + parseInt(bp_140) + parseInt(tia_features) + parseInt(duration) + parseInt(diabetes);
if (u < 4) {
    msg = 'Per the validation study, 0-3 points: Low Risk<br/>2-Day Stroke Risk: 1.0%<br/>7-Day Stroke Risk: 1.2%<br/>90-Day Stroke Risk: 3.1%';
}
if (u > 3 && u < 6) {
    msg = 'Per the validation study, 4-5 points: Moderate Risk<br/>2-Day Stroke Risk: 4.1%<br/>7-Day Stroke Risk: 5.9%<br/>90-Day Stroke Risk: 9.8%';
}
if (u > 5) {
    msg = 'Per the validation study, 6-7 points: High Risk<br/>2-Day Stroke Risk: 8.1%<br/>7-Day Stroke Risk: 11.7%<br/>90-Day Stroke Risk: 17.8%';
}
if (u < 4) {
    mini_msg = 'Low Risk';
}
if (u > 3 && u < 6) {
    mini_msg = 'Moderate Risk';
}
if (u > 5) {
    mini_msg = 'High Risk';
}
calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '357_ABCD2 Score_result',
    value: u.toFixed(0),
    value_text: 'points',
    message: msg
});
