data.logic:
var calc_output = [];
var u = parseFloat(face) + parseFloat(verbal) + parseFloat(body);
var msg, mini_msg, reassess;

if (u <= 2) {
    mini_msg = "Minimal or no pain";
    msg = "Minimal or no pain.";
    reassess = "<br/><br/> Consider reassessing after 30 minutes.";
} else if (u >= 3) {
    mini_msg = "Significant pain present";
    msg = "Significant pain present.";
    reassess = "<br/><br/>Consider analgesia and reassess afterward.";
}

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: mini_msg });
calc_output.push({ name: '3625_Behavioral Observational Pain Scale_result', value: u, value_text: 'points', message: msg + reassess });
