data.logic:
var calc_output = [];

var dose = (parseFloat(fibr_goal) - parseFloat(fibr_initial)) * pv / 100 / fibr_unit

if (fibr_goal - fibr_initial > 0) {
calc_output.push({
   name: 'mini',
   value: dose.toFixed(0),
   value_text: 'units',
   message: 'Cryoprecipitate dose'
});
calc_output.push({
   name: '10005_Cryo Dosing (Fibrinogen Replacement)_result',
   value: dose.toFixed(0),
   value_text: 'units',
   message: 'Cryoprecipitate dose'
});
}
else {
  calc_output.push({
   name: 'mini',
   value: '',
   value_text: '',
   message: 'Goal fibrinogen should be higher than initial'
});
calc_output.push({
   name: '10005_Cryo Dosing (Fibrinogen Replacement)_result',
   value: '',
   value_text: '',
   message: 'Goal fibrinogen should be higher than initial'
});
}
