data.logic:
var calc_output = [];

var age = parseFloat(age)
var heartrate = parseFloat(heartrate)
var systolic = parseFloat(systolic)

//equations
var si = heartrate / systolic
var SI = si.toFixed(2)
var msg
var risk
var msg2

if (age >= 4 && age <= 6){
	if (SI > 1.22) {
	msg = 'Elevated'
    risk = '22.0'
    msg2 = 'Maximum normal SIPA for age of ' + age + ' years is 1.22'
    }
    else {
    msg = 'Normal'
   	msg2 = 'Maximum normal SIPA for age of ' + age + ' years is 1.22'
    }
}
else if (age > 6 && age <= 12){
	if (SI > 1.0) {
	msg = 'Elevated'
    risk = '25.1'
    msg2 = 'Maximum normal SIPA for age of ' + age + ' years is 1.0'
    }
    else {
    msg = 'Normal'
    msg2 = 'Maximum normal SIPA for age of ' + age + ' years is 1.0'
    }
}
else if (age > 12){
	if (SI > 0.9) {
	msg = 'Elevated'
    risk = '32.0'
    msg2 = 'Maximum normal SIPA for age of ' + age + ' years is 0.9'
    }
    else {
    msg = 'Normal'
    msg2 = 'Maximum normal SIPA for age of ' + age + ' years is 0.9'

    }
}

//results
calc_output.push({
    name: 'mini',
    value: SI,
    value_text: '',
    message: msg + ' SIPA'
});
calc_output.push({
    name: 'SIPA',
    value: SI,
    value_text: '',
    message: 'SIPA'
});
calc_output.push({
    name: 'normal',
    value: msg,
    value_text: '',
    message: msg2
});

if (msg == 'Elevated'){
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '%',
    message: 'Risk of blunt injury'
});
}
