data.logic:
var calc_output = [];


var u, msg, pcntm, pcntr;
u =  parseFloat(age)+parseFloat(shock)+parseFloat(comor);

switch (u) {
case 0: pcntm = "0.2"; break;
case 1: pcntm = "2.4"; break;
case 2: pcntm = "5.6"; break;
case 3: pcntm = "11"; break;
case 4: pcntm = "24.6"; break;
case 5: pcntm = "39.6"; break;
case 6: pcntm = "48.9"; break;
case 7: pcntm = "50"; break;
default: pcntm = "50"; break;
}

msg = pcntm + "% mortality prior to endoscopy.";

calc_output.push({ name: 'mini', value: u, value_text: 'points', message: msg });
calc_output.push({ name: '3643_Rockall Score (Pre-Endoscopy)_result', value: u, value_text: 'points', message: msg });
