data.logic:
var calc_output = []; 
var msg='';
var mini_msg='';
var u=parseInt(age_55)+parseInt(patella_tenderness)+parseInt(fibular_tenderness)+parseInt(knee_flex_unable)+parseInt(bear_weight_unable);

if(u==0){
    mini_msg='Imaging not required'; 
    msg='If no criteria are met, according to the Ottawa Knee Rules, imaging is not indicated.'
} else if(u>0){
    mini_msg='Consider imaging'; 
    msg='If any of the above criteria are met, this patient may need knee imaging: the rule is sensitive to rule-out fractures, but not specific to suggest who may have a fracture.'
} 

calc_output.push({
    name:'mini',
    value:' ',
    value_text:' ',
    message:mini_msg
}); 
calc_output.push({
    name:'368_Ottawa Knee Rule_result',
    value:' ',
    value_text:' ',
    message:msg
});
