data.logic:
var calc_output = [];

var u = parseFloat(ncct) + parseFloat(age) + parseFloat(sex) + parseFloat(htn);  


if (u == 0) {msg = '0';}
if (u == 1) {msg = '1.5';}
if (u == 2) {msg = '4.4';}
if (u == 3) {msg = '20';}
if (u == 4) {msg = '38.1';}
if (u == 5) {msg = '80';}
if (u == 6) {msg = '100';}

calc_output.push({ 
	name: 'mini', 
	value: u, 
	value_text: 'points', 
	message: msg+'% of CTA positive for vascular cause of ICH' 
});
calc_output.push({ 
	name: '3802_sICH Score_result', 
	value: u, 
	value_text: 'points', 
	message: msg+'% of CTA positive for vascular cause of ICH' 
});
