data.logic:
var calc_output = [];

var weight = parseFloat(weight)
var percent = parseFloat(percent)
var amount = parseFloat(amount)

var c, dose

dose = percent * 10 * amount
c = dose / (0.6 * weight) * 0.1;

var type_name, conversion, treat
if (type == 0) {
    type_name = 'ethanol'
    conversion = c/4.6
    treat = ''
}
if (type == 1) {
    type_name = 'methanol'
    conversion = c/3.2
    treat = '. Treatment with fomepizole recommended at methanol concentration ≥20 mg/dL (6.2 mmol/L). See Next Steps for details.'
}
if (type == 2) {
    type_name = 'ethylene glycol'
    conversion = c/6.2
    treat = '. Treatment with fomepizole recommended at ethylene glycol concentration ≥20 mg/dL (3.2 mmol/L). See Next Steps for details.'
}
if (type == 3) {
    type_name = 'diethylene glycol'
    conversion = c/1
    treat = '. Treatment with fomepizole recommended at any concentration of diethylene glycol. See Next Steps for details.'
}

calc_output.push({
    name: 'mini',
    value: c.toFixed(0),
    value_text: 'mg/dL',
    message: 'Estimated serum concentration'
});
calc_output.push({
    name: '3813_Estimated EtOH_result (serum conc)', 
    value: c.toFixed(0),
    value_text: 'mg/dL',
    message: 'Estimated serum concentration<br/>(' + conversion.toFixed(1) + ' mmol/L)' + treat
});
calc_output.push({
    name: '3813_Estimated EtOH_result (dose)',
    value: dose/1000,
    value_text: 'g',
    message: 'Dose of ' + type_name
});
