data.logic:
var calc_output = [];

var u = parseInt(cxr) + parseInt(distracting) + parseInt(ttp);
var v = parseInt(deceleration);
var p = u + v;

var mini_msg, msg;

if (u > 0 && v == 0) {
    mini_msg = 'Cannot exclude thoracic injury';
    msg = 'Cannot exclude thoracic injury; consider further workup including chest CT';
    msg2 = '';
} else if (u > 0 && v > 0) {
    mini_msg = 'Cannot exclude thoracic injury';
    msg = 'Cannot exclude thoracic injury; consider further workup including chest CT';
    msg2 = '';
} else if (u == 0 && v == 1) {
    mini_msg = 'Cannot exclude thoracic injury';
    msg = 'Cannot exclude thoracic injury; consider further workup including chest CT';
    msg2 = '<br/><br/>Major or minor injury: 95.4% sensitive, 25.5% specific';
} else {
    mini_msg = 'No Chest CT required by NEXUS Chest';
    msg = 'All MAJOR AND MINOR criteria are ABSENT - no Chest CT by NEXUS Chest.';
    msg2 = '<br/><br/>Major injury: 99.2% sensitive, 31.7% specific<br/>Major or minor injury: 90.7% sensitive, 37.9% specific';
}


calc_output.push({ name: 'mini', value: '', value_text: '', message: mini_msg });
calc_output.push({ 
    name: '3815_NEXUS Chest CT Decision Instrument_result', 
    value: '', 
    value_text: '', 
    message: msg + msg2 
});
