data.logic:
var calc_output = [];

var age = parseFloat(age);
var tchol = parseFloat(tchol);
var ldl = parseFloat(ldl);
var xan = parseFloat(xan);
var dna = parseFloat(dna);
var cvd = parseFloat(cvd);
var fam = parseFloat(fam);

if (UOMSYSTEM == false) {
    tchol = tchol / 0.0259
    ldl = ldl / 0.0259
}
else {
    tchol = tchol
    ldl = ldl
}

var string1, tchol, ldl, tchol, ldl;
var string2 = 'Familial Hypercholesterolemia';

if (age == 2) {
    if (tchol >= 288) { tchol = 1; } else { tchol = 0; }
    if (ldl >= 188) { ldl = 1; } else { ldl = 0; }
}
if (age == 1) {
    if (tchol >= 258) { tchol = 1; } else { tchol = 0; }
    if (ldl >= 154) { ldl = 1; } else { ldl = 0; }
}

if (tchol + ldl >= 1) {
    if (xan + dna + cvd + fam == 0) { string1 = 'Unlikely'; }
    if (xan + dna >= 1) { string1 = 'Definite'; }
    if (xan + dna == 0) {
        if (cvd + fam >= 1) { string1 = 'Probable'; }
        }
    }
else if (tchol + ldl == 0) { string1 = 'Unlikely'; }

calc_output.push({
    name: 'mini',
    value: '',
    value_text: '',
    message: string1 + ' ' + string2
});
calc_output.push({
    name: '3817_Simon Broome Diagnostic Criteria_result',
    value: string1,
    value_text: '',
    message: string2
});
