data.logic:
var calc_output = [];

var line = parseFloat(line)

var acuity;
var a_meter;

if (line == 0){
  acuity = '20/200'
  a_meter = '6/60'
  msg = 'Visual acuity worse than 20/25 (6/7.5 if non-US) should be evaluated by a licensed eye professional to determine whether corrective lenses or other treatments may be necessary'
}
else if (line == 1){
	acuity = '20/100'
    a_meter = '6/30'
    msg = 'Visual acuity worse than 20/25 (6/7.5 if non-US) should be evaluated by a licensed eye professional to determine whether corrective lenses or other treatments may be necessary'
}
else if (line == 2){
	acuity = '20/70'
    a_meter = '6/21'  
    msg = 'Visual acuity worse than 20/25 (6/7.5 if non-US) should be evaluated by a licensed eye professional to determine whether corrective lenses or other treatments may be necessary'
}
else if (line == 3){
	acuity = '20/50'
    a_meter = '6/15'  
    msg = 'Visual acuity worse than 20/25 (6/7.5 if non-US) should be evaluated by a licensed eye professional to determine whether corrective lenses or other treatments may be necessary'
}
else if (line == 4){
	acuity = '20/40'
    a_meter = '6/12'  
    msg = 'Visual acuity worse than 20/25 (6/7.5 if non-US) should be evaluated by a licensed eye professional to determine whether corrective lenses or other treatments may be necessary'
}
else if (line == 5){
	acuity = '20/30'
    a_meter = '6/9'  
    msg = 'Visual acuity worse than 20/25 (6/7.5 if non-US) should be evaluated by a licensed eye professional to determine whether corrective lenses or other treatments may be necessary'
}
else if (line == 6){
  acuity = '20/25';
  a_meter = '6/7.5'  
  msg = '';
}
else {
  acuity = '20/20';
  a_meter = '6/6'  
  msg = '';
}

//results 
calc_output.push({
    name: 'mini',
    value: acuity,
    value_text: '',
    message: 'Equivalent to ' + a_meter
    });
calc_output.push({
    name: '10060_Snellen Chart_result',
    value: acuity,
    value_text: '',
    message: 'Visual acuity<br/><br/>Equivalent to ' + a_meter + ' (non-US)'
    });
if(msg != ''){
calc_output.push({
    name: '10060_Snellen Chart_interpretation',
    value: '',
    value_text: '',
    message: msg
    });
}
