var cad = cad || 0

var calc_output = [];

var fever = parseFloat(fever);
acute = parseFloat(acute);
subacute = parseFloat(subacute);
exanth = parseFloat(exanth);
conjunct = parseFloat(conjunct);
lips = parseFloat(lips);
lad = parseFloat(lad);
cad = parseFloat(cad);
var acuity;

if (acute == 1 || subacute == 1) {
    acuity = 1
}
else {
    acuity = 0
}

var score = acuity + exanth + conjunct + lips + lad;

if (fever == 1) {
    if (score >= 4) {
        string = 'Positive';
    } else if (score < 4 && cad == 1) {
        string = 'Positive';
    } else if (score == 2 || score == 3) {
        string = 'Incomplete'
    } else {
        string = 'Negative';
    }
} else {
    string = 'Negative';
}
if (fever == 0 && score >= 4) {
    string = 'Positive'
    }

