data.logic:
var bhx = bhx || 0
var chx = chx || 0
var dhx = dhx || 0

var calc_output = [];

var ahx = parseFloat(ahx);
var ctox = parseFloat(ctox);
var cm = parseFloat(cm);
var a = ahx + ctox + cm;
var b = parseFloat(bhx);
var c = parseFloat(chx);
var d = parseFloat(dhx);
var stage, msg, therapy;

if (a >= 1) {
    stage = 'A';
    msg = 'At high risk for heart failure without structural heart disease or symptoms of HF';
    therapy = '<strong>Therapy considerations:</strong><br/>Treat hypertension<br/>Encourage smoking cessation<br/>Treat lipid disorders<br/>Encourage regular exercise<br/>Discourage alcohol intake/illicit drug use<br/>ACE inhibitors in appropriate patients';
    if (b == 1) {
        stage = 'B';
        msg = 'Structural heart disease without symptoms of HF;';
        therapy = '<strong>Therapy considerations:</strong><br/>All measures under stage A<br/>ACE inhibitors in appropriate patients<br/>Beta-blockers in appropriate patients';
        if (c == 1) {
            stage = 'C';
            msg = 'Structural heart disease with prior or current symptoms of HF';
            therapy = '<strong>Therapy considerations:</strong><br/>All measures under stage A<br/>Diuretics, ACE inhibitors, beta-blockers, or digitalis for routine use<br/>Dietary salt restriction';
            if (d == 1) {
                stage = 'D';
                msg = 'Refractory HF requiring specialized interventions';
                therapy = '<strong>Therapy considerations:</strong><br/>All measures under stage A, B and C<br/>Mechanical assist devices<br/>Heart transplantation<br/>Continuous IV inotropic infusions for palliation<br/>Hospice care';
            } else {
                stage = 'C';
                msg = 'Structural heart disease with prior or current symptoms of HF';
                therapy = '<strong>Therapy considerations:</strong><br/>All measures under stage A<br/>Diuretics, ACE inhibitors, beta-blockers, or digitalis for routine use<br/>Dietary salt restriction';
            }
        } else {
            stage = 'B';
            msg = 'Structural heart disease without symptoms of HF;';
            therapy = '<strong>Therapy considerations:</strong><br/>All measures under stage A<br/>ACE inhibitors in appropriate patients<br/>Beta-blockers in appropriate patients';
        }
    } else {
        stage = 'A';
        msg = 'At high risk for heart failure without structural heart disease or symptoms of HF';
        therapy = '<strong>Therapy considerations:</strong><br/>Treat hypertension<br/>Encourage smoking cessation<br/>Treat lipid disorders<br/>Encourage regular exercise<br/>Discourage alcohol intake/illicit drug use<br/>ACE inhibitors in appropriate patients';

    }
} else {
    stage = 'N/A';
    msg = 'Patient does not have substantial risk of HF based ACC/AHA staging guidelines.';
    therapy = '';
}


calc_output.push({ 
    name: 'mini', 
    value: 'Stage: ' + stage, 
    value_text: ' ', 
    message: ' ' 
});
calc_output.push({ 
    name: '3826_ACC/AHA Heart Failure Staging_result', 
    value: 'Stage: ' + stage, 
    value_text: '', message: 
    msg + '<br/><br/>' + therapy 
});
