data.logic:
var calc_output = [];

var grade, msg, mini_msg;
var sah=parseFloat(sah);
var ivh=parseFloat(ivh);

if (sah == 0 && ivh == 0) { grade = 'Grade 0';  msg = 'No subarachnoid hemorrhage and no intraventricular blood.'; }
else if (sah == 0 && ivh == 1) { grade = 'N/A';  msg = 'IVH cannot be present if SAH is not present for the Modified Fisher Scale.'; }
else if (sah == 1 && ivh == 0) { grade = 'Grade I';  msg = '6-24% probability of symptomatic vasospasm.'; }
else if (sah == 1 && ivh == 1) { grade = 'Grade II'; msg = '15-33% probability of symptomatic vasospasm.'; }
else if (sah == 2 && ivh == 0) { grade = 'Grade III';  msg = '33-35% probability of symptomatic vasospasm.'; }
else if (sah == 2 && ivh == 1) {grade = 'Grade IV';  msg = '34-40% probability of symptomatic vasospasm.';  }


calc_output.push({
    name: 'mini',
    value: grade,
    value_text: ' ',
    message: msg
});
calc_output.push({
    name: '3837_Modified Fisher Scale_result',
    value: grade,
    value_text: '',
    message: msg
});
