data.logic:
var calc_output = [];
var calc_msg = '';
var mini_msg = '';
var yesno = '';
var output_score = parseInt(age) + parseInt(neckpain) + parseInt(loc) + parseInt(exertion) + parseInt(thunderclap) + parseInt(neckflexion);
if (output_score == 0) {
    yesno = 'Yes';
    calc_msg = 'This patient can be ruled out for subarachnoid hemorrhage by the Ottawa SAH Rule, which was 100% sensitive for SAH in its validation.';
    mini_msg = 'Ruled Out';
} else {
    yesno = 'No';
    calc_msg ='This patient cannot be ruled out for subarachnoid hemorrhage by the Ottawa SAH Rule (the rule was 100% sensitive for SAH but only 15% specific for SAH).';
    mini_msg = 'Cannot Rule Out';
}
calc_output.push({
    name: 'mini',
    value: yesno,
    value_text: ' ',
    message: mini_msg
});
calc_output.push({
    name: '3875_Ottawa SAH Rule_result',
    value: mini_msg,
    value_text: ' ',
    message: calc_msg
});
