data.logic:
var calc_output = [];

var u = Math.max(parseFloat(onetumor), parseFloat(threetumor)) + parseFloat(extrahep) + parseFloat(majorvess);
var msg, mini_msg;
if (u < 3) {
  msg = 'Criteria not met';
  mini_msg = 'Milan Criteria NOT fulfilled. Patient is NOT a viable transplant candidate.';
} else {
  msg = 'Criteria met';
  mini_msg = 'Milan Criteria fulfilled. Patient is a viable transplant candidate.';
}
calc_output.push({ 
	name: 'mini', 
	value: '', 
	value_text: '', 
	message: mini_msg 
});
calc_output.push({ 
	name: '3900_Milan Criteria_result', 
	value: msg, 
	value_text: '', 
	message: mini_msg 
});
