data.logic:
var calc_output = [];
var v = parseFloat(behavior) + parseFloat(cardiovascular) + parseFloat(respiratory) + parseFloat(neb) + parseFloat(vom);
var msg, mini_msg, msg2;
if (parseFloat(behavior) == 3 || parseFloat(cardiovascular) == 3 || parseFloat(respiratory) == 3) {
    msg2 = "For any single parameter with assigned score of 3, consider escalation of care.";
} else {
    msg2 = '';
}

if (v == 0 || v == 1 || v == 2) {
    msg = "Low risk for deterioration. Reassess as needed.";
    mini_msg = "Low risk for deterioration";
} if (v == 3 || v == 4) {
    msg = "Intermediate risk for deterioration. Consider escalation of care and increased frequency of monitoring.";
    mini_msg ="Intermediate risk for deterioration";
} if (v >= 5) {
    msg = "High risk for deterioration. Consider transfer to a monitored setting and other necessary interventions as needed.";
    mini_msg ="High risk for deterioration";
}


calc_output.push({ name: 'mini', value: v.toFixed(0), value_text: 'points', message: mini_msg });
calc_output.push({ 
    name: '3901_PEWS Score_result', 
    value: v.toFixed(0), 
    value_text: 'points', 
    message: msg + '<br/><br/>' + msg2
});
