data.logic:
var calc_output = [];

var age = parseFloat(age);
var agescore, pfo, stroke, stroke_msg, pfo_msg;
var u;
if (age >= 18 && age <= 29) {
    agescore = 5;
}
if (age >= 30 && age <= 39) {
    agescore = 4;
}
if (age >= 40 && age <= 49) {
    agescore = 3;
}
if (age >= 50 && age <= 59) {
    agescore = 2
}
if (age >= 60 && age <= 69) {
    agescore = 1;
}
if (age >= 70) {
    agescore = 0;
}

if (age < 18) {
    pfo = '';
    stroke = '';
    pfo_msg = 'This patient is not in the appropriate age range for this calculator.';
    stroke_msg = 'This patient is not in the appropriate age range for this calculator.';
    u = 'N/A';
} else {
    u = agescore + parseFloat(htn) + parseFloat(dm) + parseFloat(tia) + parseFloat(nonsmoker) + parseFloat(infarct)
    var pfo_msg = ' chance that stroke is due to PFO.';
    var stroke_msg = ' risk of 2 year recurrence of stroke/TIA.';
    if (u <= 3) {
        pfo = '0%';
        stroke = '20%';
    }
    if (u == 4) {
        pfo = '38%';
        stroke = '12%';
    }
    if (u == 5) {
        pfo = '34%';
        stroke = '7%';
    }
    if (u == 6) {
        pfo = '62%';
        stroke = '8%';
    }
    if (u == 7) {
        pfo = '72%';
        stroke = '6%';
    }
    if (u == 8) {
        pfo = '84%';
        stroke = '6%';
    }
    if (u >= 9) {
        pfo = '88%';
        stroke = '2%';

    }
}

calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: pfo + pfo_msg
});
calc_output.push({
    name: '3902_RoPE Score_result',
    value: u,
    value_text: 'points',
    message: pfo + pfo_msg + (pfo_msg == stroke_msg ? "" : "<br/><br/>" + stroke + stroke_msg)
});
