data.logic:
var calc_output = [];
var care = parseFloat(care);
var e = parseFloat(e);
var grade;
var morb, mort;
var perc = '%';
if (care == 1) {
    grade = '1';
    morb = '5.4';
    mort = '0.5';
}
if (care == 2) {
    grade = '2';
    morb = '10.3';
    mort = '1.1';
}
if (care == 3) {
    if (e == 0) {
        grade = '3';
        morb = '19.0';
        mort = '2.2';
    } else {
        grade = '3E';
        morb = '32.1';
        mort = '4.5';
    }
}
if (care == 4) {
    if (e == 0) {
        grade = '4';
        morb = '48.8';
        mort = '8.8';
    } else {
        grade = '4E';
        morb = '65.8';
        mort = '16.7';
    }
}
if (care == 5) {
    if (e == 0) {
        grade = '5';
        morb = '79.6';
        mort = '29.3';
    } else {
        grade = '5E';
        morb = '88.7';
        mort = ' 46.2';
    }
}
calc_output.push({ name: 'mini', value: 'CARE '+grade, value_text: '', message: mort+perc+' in-hospital mortality' });
calc_output.push({ 
    name: '3903_Cardiac Anesthesia Risk Evaluation Score (CARE)_result', 
    value: 'CARE '+grade, 
    value_text: '', 
    message: mort + perc + ' in-hospital mortality<br/><br/>' + morb+ perc+' morbidity (See Formula section for morbidity definitions.)'  });
