data.logic:
var calc_output = [];

var nodule = parseFloat(nodule)
var type = parseFloat(type)
var level = parseFloat(level)
var features = parseFloat(features)
var size1 = parseFloat(size1)
var size2 = parseFloat(size2)

var result = ''
var msg = ''

if (nodule == 0){
  	if (level == 0){
    	if (type == 0 && size1 == 0) {
	result = ''
    msg = 'No routine follow-up'
   		}
    	else if (type == 0 && size1 == 1) {
	result = ''
	msg = 'CT at 6-12 months, then consider CT at 18-24 months'
    	}
    	else if (type == 0 && size1 == 2) {
	result = ''
	msg = 'Consider CT at 3 months, PET/CT, or tissue sampling'
    	}
    	else if (type == 1 && size1 == 0) {
	result = ''
	msg = 'No routine follow-up'
    	}
    	else if (type == 1 && size1 == 1) {
	result = ''
	msg = 'CT at 3-6 months, then consider CT at 18-24 months'
    	}
    	else if (type == 1 && size1 == 2) {
	result = ''
	msg = 'CT at 3-6 months, then consider CT at 18-24 months'
    	}
    }

  	else if (level == 1){
    	if (type == 0 && size1 == 0) {
	result = ''
    msg = 'Optional CT at 12 months'
   		}
    	else if (type == 0 && size1 == 1) {
	result = ''
	msg = 'CT at 6-12 months, then CT at 18-24 months'
    	}
    	else if (type == 0 && size1 == 2) {
	result = ''
	msg = 'Consider CT at 3 months, PET/CT, or tissue sampling'
    	}
    	else if (type == 1 && size1 == 0) {
	result = ''
	msg = 'Optional CT at 12 months'
    	}
    	else if (type == 1 && size1 == 1) {
	result = ''
	msg = 'CT at 3-6 months, then at 18-24 months'
    	}
    	else if (type == 1 && size1 == 2) {
	result = ''
	msg = 'CT at 3-6 months, then at 18-24 months'
    	}
    }
}
else if (nodule == 1){
  	if (type == 0){
    	if (features == 0 && size2 == 0) {
	result = ''
    msg = 'No routine follow-up'
   		}
    	else if (features == 0 && size2 == 1) {
	result = ''
	msg = 'CT at 6-12 months to confirm persistence, then CT every 2 years until 5 years'
    	}
    	else if (features == 1 && size2 == 0) {
	result = ''
	msg = 'No routine follow-up'
    	}
    	else if (features == 1 && size2 == 1) {
	result = ''
	msg = 'CT at 3-6 months to confirm persistence. If unchanged and solid component remains <6 mm, annual CT should be performed for 5 years'
        }
    }

  	else if (type == 1){
    	if (size2 == 0) {
	result = ''
    msg = 'CT at 3-6 months. If stable, consider CT at 2 and 4 years'
   		}
    	else if (size2 == 1) {
	result = ''
	msg = 'CT at 3-6 months. Subsequent management based on the most suspicious nodule(s)'
    	}
    }
}

calc_output.push({
