data.logic:
var calc_output = [];

var spl = parseFloat(spl);
var baso = parseFloat(baso);
if (UOMSYSTEM == false) {
  spl = spl*2.54;
}

var msg, mini_msg;
var score = (7 * baso) + (4 * spl);

var x = Math.exp(-2.1007 + (0.0700 * baso) + (0.0402 * spl));
noCCgR = x / (1 + x);
noCCgR = parseInt(noCCgR * 100);

var msg = 'Estimated probability of <u>not</u> achieving complete cytogenic response at 18 months: ' + noCCgR + '%';
var msg1;
var msg2;
var msg3;

switch (true) {
    case (score < 87):
        mini_msg = 'Low risk group';
        msg1 = 'Low';
        msg2 = '86%';   //CCyR at 18 months
        msg3 = '90';  // % 5-year PFS
        break;
    case (score >= 87):
        mini_msg = 'High risk group';
        msg1 = 'High';
        msg2 = '66%';       //CCyR at 18 months
        msg3 = '82';      // % 5-year PFS
        break;
    default:
        console.log('error');
}

calc_output.push({
    name: 'mini',
    value: score.toFixed(0),
    value_text: 'points',
    message: mini_msg
});
calc_output.push({
    name: '3906_EUTOS Score_result (score)',
    value: score.toFixed(0),
    value_text: 'points',
    message: 'EUTOS Score'
});
calc_output.push({
    name: '3906_EUTOS Score_interpretation',
    value: msg1,
    value_text: 'risk',
    message: msg3 + '% 5-year progression-free survival <br/>' + msg2 + ' complete cytogenic response (CCyR) at 18 months'
});
calc_output.push({
    name: '3906_EUTOS Score_result (risk)',
    value: noCCgR,
    value_text: '%',
    message: 'Likelihood of NOT achieving CCyR'
});
