data.logic:
var calc_output = [];
var radius = parseFloat(radius);
var exo = parseFloat(exo);
var nearness = parseFloat(nearness);
var ant = parseFloat(ant);
var hilum = parseFloat(hilum);
var loc = parseFloat(loc);

if (loc == 3 || loc == 4 || loc == 5) {
	loc = 3
}

var p = radius + exo + nearness + loc;
var ht = ['', 'h'];
var at = ['a', 'p', 'x'];
var score = p + at[ant] + ht[hilum];
var msg;
var complexity;
var complication;

if (p >= 4 && p <= 6) {
	complexity = 'Low';
	complication = '6.4'
}
if (p >= 7 && p <= 9) {
	complexity = 'Intermediate';
	complication = '11.1'
}
if (p >= 10 && p <= 12) {
	complexity = 'High';
	complication = '21.9'
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: '',
    message: 'RENAL Nephrometry Score'
});
calc_output.push({
    name: '3908_RENAL Nephrometry Score_result (score)',
    value: score,
    value_text: '',
    message: 'RENAL Nephrometry Score'
});
calc_output.push({
    name: '3908_RENAL Nephrometry Score_interpretation',
    value: complexity,
    value_text: '',
    message: 'complexity'
});
calc_output.push({
    name: '3908_RENAL Nephrometry Score_result (risk)',
    value: complication,
    value_text: '%',
    message: 'Likelihood of major complication (see Facts & Figures for definitions)'
});
