data.logic:
var calc_output = [];

var burden = parseFloat(burden);
var bp = parseFloat(bp);
var copd = parseFloat(copd);
var solid = parseFloat(solid);
// var fung = parseFloat(fung)
var hyd = parseFloat(hyd);
var outpt = parseFloat(outpt);
var age = parseFloat(age);

var u = burden + bp + copd + solid + hyd + outpt + age;

// if (solid == 4) {
// 	u = burden + bp + copd + solid + hyd + outpt + age
// }
// if (solid == 0){
// 	u = burden + bp + copd + solid + hyd + outpt + age + parseFloat(fung)
// }

var msg;
var rec;

if (u >= 21) {
	msg = "Low risk for poor outcome."; 
	rec = " Consider oral and/or outpatient empiric antibiotic therapy after discussion with oncology.";
}
if (u < 21) {
	msg = "High risk for poor outcome.";
	rec = " Admit for empiric antibiotics if not already inpatient.";
}

calc_output.push({ 
	name: 'mini', 
	value: u, 
	value_text: 'points', 
	message: msg 
});
calc_output.push({ 
	name: '3913_MASCC Risk Index_result', 
	value: u, 
	value_text: 'points', 
	message: msg + rec
});
