data.logic:
var calc_output = [];

var race = parseFloat(race)
var hn = parseFloat(hn)
var uti = parseFloat(uti)
var obs = parseFloat(obs)
var chf = parseFloat(chf)
var aki = parseFloat(aki)
var exposure = parseFloat(exposure)

var score = race + uti + obs + chf + aki + exposure

var group 
var prev
var risk

if (hn == 1) {
score = ''
group = 'High'
prev = '20.9'
risk = '4.9'
}

else {
  if (score <= 2) {
	group = 'Low'
  	prev = '4.0'
    risk = '1.1'
  }
  else if (score == 3) {
    group = 'Medium'
  	prev = '6.8'
    risk = '0.5'
  }
  else if (score > 3) {
    group = 'High'
  	prev = '20.9'
	risk = '4.9'
  }
}

//results

if (hn == 0) {
	calc_output.push({
    name: 'mini',
    value: risk,
    value_text: '%',
    message: 'Risk of hydronephrosis requiring intervention'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Licurse Score'
});

calc_output.push({
    name: 'group',
    value: group,
    value_text: 'risk',
    message: ''
});
}

if (hn == 1) {
	calc_output.push({
    name: 'mini',
    value: 'High',
    value_text: 'risk',
    message: 'Hydronephrosis history automatically confers high risk'
});

calc_output.push({
    name: 'group',
    value: group,
    value_text: 'risk',
    message: 'History of hydronephrosis automatically confers high risk'
});
}

calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '%',
    message: 'Risk of hydronephrosis requiring stent or nephrostomy (' + prev + '% risk of hydronephrosis on imaging)'
});
