data.logic:
var calc_output = [];

var u = parseFloat(nodes) + parseFloat(interval) + parseFloat(tumor) + parseFloat(cea) + parseFloat(size);
var surv1;
var surv5;
var risk;
var msg;

// 1- and 5-year survival
if (u==0) {surv1 = '93'; surv5 = '60';}
if (u==1) {surv1 = '91'; surv5 = '44';}
if (u==2) {surv1 = '89'; surv5 = '40';}
if (u==3) {surv1 = '86'; surv5 = '20';}
if (u==4) {surv1 = '70'; surv5 = '25';}
if (u==5) {surv1 = '71'; surv5 = '14';}

// risk categories
if (u == 0 || u == 1 || u == 2) {risk = 'Low risk'; msg = 'Consider surgical intervention'}
if (u == 3 || u == 4 || u == 5 || u == 6) {risk = 'High risk'; msg = 'Consider additional preop workup and alternatives including close surveillance'}

// result on app
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: surv1 + ' survival at 1 year'
});

// result on web
calc_output.push({
    name: '3925_Fong Score_result (score)',
    value: u,
    value_text: 'points',
    message: risk + ': ' + msg 
})

calc_output.push({
    name: '3925_Fong Score_result (1 yr)',
    value: surv1,
    value_text: '%',
    message: '1-year survival after metastasectomy'
});

calc_output.push({
    name: '3925_Fong Score_result (5 yrs)',
    value: surv5,
    value_text: '%',
    message: '5-year survival after metastasectomy'
});
