data.logic:
var calc_output = [];
var height = parseFloat(height);
var gender  = parseFloat(gender);
var ibw;
var tv;

if (UOMSYSTEM == true) { // US units
  height = parseFloat(height)
}
else { // SI units
  height = parseFloat(height) / 2.54
}

if (height >= 60 && gender == 0) { // women taller than 60 inches
	ibw = (45.5 + 2.3 * (height - 60))
} 
if (height <= 60 && gender == 0) { // women 60 inches or shorter
	ibw = 45.5
} 
if (height >= 60 && gender == 1) { // men taller than 60 inches
	ibw = (50 + 2.3 * (height - 60))
} 
if (height <= 60 && gender == 1) { // men 60 inches or shorter
	ibw = 50
} 

var depth = (0.1 * height * 2.54 + 4);

calc_output.push({
  name: 'mini',
  value: depth.toFixed(0),
  value_text: ' cm',
  message: 'ETT depth'
});
calc_output.push({
  name: '3928_ETT Depth and Tidal Volume_result (depth)',
  value: depth.toFixed(0),
  value_text: ' cm',
  message: 'ETT depth from front teeth'
});
calc_output.push({
  name: '3928_ETT Depth and Tidal Volume_result (target 6)',
  value: 6 * ibw.toFixed(0),
  value_text: ' mL',
  message: 'Tidal volume if target is 6 mL/kg, IBW '  + ibw.toFixed(0) + ' kg'
});
  calc_output.push({
  name: '3928_ETT Depth and Tidal Volume_result (target 8)',
  value: 8 * ibw.toFixed(0),
  value_text: ' mL',
  message: 'Tidal volume if target is 8 mL/kg, IBW ' + ibw.toFixed(0) + ' kg'
});
