data.logic:
// Asymptomatic Myeloma Prognosis
var calc_output = [];

var bmp; 				// bone marrow plasmacytosis 
var smp; 				// serum monoclonal protein
var risk_group = '';	// risk group
var risk_percent = '';	// risk in percentage
var time = '';			// time to progression in years
var msg = ''; 			// message

if (bmp == 0 && smp == 0) {
	risk_group = 'High';
	risk_percent = '69%'; 
	msg = 'Risk of progression at 5 years<br/><br/><strong>77%</strong> at 10 years<br/><strong>87%</strong> at 15 years';
	time = '27';
}
if (bmp == 0 && smp == 1) {
	risk_group = 'Intermediate';
	risk_percent = '43%'; 
	msg = 'Risk of progression at 5 years<br/><br/><strong>64%</strong> at 10 years<br/><strong>70%</strong> at 15 years';
	time = '93';
}

if (bmp == 1 && smp == 0) {
	risk_group = 'Low';
	risk_percent = '15%';
	msg = 'Risk of progression at 5 years<br/><br/><strong>33%</strong> at 10 years<br/><strong>39%</strong> at 15 years'; 
	time = '228';
}
if (bmp == 1 && smp == 1) {
	risk_group = '';
	risk_percent = ''; 
	msg = 'Patient does not meet criteria for asymptomatic myeloma (see Pearls/Pitfalls for criteria)';
	time = '';
}
if (bmp + smp < 2) {
calc_output.push({ // message on app
    name: 'mini',
    value: risk_group,
    value_text: 'risk',
    message: ""
});
}
if (bmp == 1 && smp == 1) {
calc_output.push({ // message on app
    name: 'mini',
    value: '',
    value_text: '',
    message: msg
});
}
if (bmp + smp < 2) {
calc_output.push({
    name: 'risk_group',
    value: risk_group,
    value_text: ' risk',
    message: ""
});
}
calc_output.push({
    name: 'risk',
    value: risk_percent,
    value_text: '',
    message: msg
});
if (bmp + smp < 2) {
calc_output.push({
    name: 'time',
    value: time,
    value_text: ' months',
    message: 'Median time to progression'
});
}
