data.logic:
var hgba1c = hgba1c || 0;

var calc_output = [];

// Declare vars
var age = parseFloat(age);
var sbp = parseFloat(sbp);
var dm = parseFloat(dm);
var smoker = parseFloat(smoker);
var chol_hdl = parseFloat(chol_hdl);
var chol = parseFloat(chol);
var hscrp = parseFloat(hscrp);
var famhx = parseFloat(famhx);
var hgba1c = parseFloat(hgba1c);

var msg; 

msg = '10 year risk of MI or stroke';

if (UOMSYSTEM == true) {
    chol_hdl = chol_hdl;
	chol = chol;
	hscrp = hscrp;}
if (UOMSYSTEM == false) {
    chol_hdl = chol_hdl/0.02586;
	chol = chol/0.02586;
	hscrp = hscrp*10;}


//if (dm == 1) {
var b = 0.0799*age + 3.137*Math.log(sbp) + 0.180*Math.log(hscrp) + 1.382*Math.log(chol) - 1.172*Math.log(chol_hdl) + 0.134*hgba1c + 0.818*smoker + 0.438*famhx;

//} else {
//	b = 0.0799*age + 3.137*Math.log(sbp) + 0.180*Math.log(hscrp) + 1.382*Math.log(chol) - //1.172*Math.log(chol_hdl) + 0.134*hgbalc + 0.818*smoker + 0.438*famhx;}

var score = (1 - Math.pow(0.98634, Math.exp(b - 22.325))) * 100;

var msg2;

if (score < 5){
  msg2 = 'Unclear evidence for statin therapy';
}
else if (score >= 5 && score < 10){
    msg2 = 'Minimal benefit of statin therapy compared to risk and cost of therapy in preventing a cardiovascular event in the next ten years';
}
else if (score >= 10 && score < 20){
    msg2 = 'Discussion of lifestyle modification and initiation of statin therapy';
}
else if (score >= 20){
    msg2 = 'Strongly recommend statin therapy in conjunction with lifestyle modification';
}


calc_output.push({
    name: 'mini',
    value: score.toFixed(1),
    value_text: '%',
    message: msg
});
calc_output.push({
    name: 'score',
    value: score.toFixed(1),
    value_text: '%',
    message: msg + '<br/>Recommendation: ' + msg2
});
