data.logic:
//outputs
var calc_output = [];
var rgroup, ppf;

//user inputs
var age = parseFloat(age);
var rr = parseFloat(rr);
var or = parseFloat(or);
var a = parseFloat(a);
var wb = parseFloat(wb);
var d = parseFloat(d);

//final score
var u, rrscore;

//Calculate score for resp rate depending on age range
if (age == 2 || age == 3){
	if (rr <= 34){
		rrscore = 1;
	}else if (rr >= 35 && rr <= 39){
		rrscore = 2;
	}else{
		rrscore = 3;
	}
}else if (age == 4 || age == 5){
	if (rr <= 30){
		rrscore = 1;
	}else if (rr >= 31 && rr <= 35){
		rrscore = 2;
	}else{
		rrscore = 3;
	}
}else if (age >= 6 && age <= 12){
	if (rr <= 26){
		rrscore = 1;
	}else if (rr >= 27 && rr <= 30){
		rrscore = 2;
	}else{
		rrscore = 3;
	}
}else{
	if (rr <= 23){
		rrscore = 1;
	}else if (rr >= 24 && rr <= 27){
		rrscore = 2;
	}else{
		rrscore = 3;
	}
}

//calculate final score
u = rrscore + or + a + wb + d;

if(age > 18 || age < 2){
	u = 'N/A';
	rgroup = 'N/A';
	ppf = 'N/A';
} 
else if(u < 5){
	rgroup = 'Unlikely';
	ppf = 'N/A';
}
else if(u >= 5 && u <= 7){
	rgroup = 'Mild';
	ppf = ">70";
}else if(u >= 8 && u <= 11){
	rgroup = 'Moderate';
	ppf = "50-70";
}else{
	rgroup = 'Severe';
	ppf = "<50";
}

//result for user 
calc_output.push({
	name: 'mini',
	value: u,
	value_text: 'points',
	message: rgroup + " Exacerbation"});

if(age > 18 || age < 2){
calc_output.push({
	name: 'score',
	value: u,
	value_text: 'points',
	message: 'This patient is not in the appropriate age range for this calculator'
});
}else{
	calc_output.push({
	name: 'score',
	value: u,
	value_text: 'points',
	message: 'Pediatric Asthma Score'
	});
}
calc_output.push({
	name: 'group',
	value: rgroup,
	value_text: '',
	message: 'Exacerbation'
});
calc_output.push({
	name: 'ppf',
	value: ppf,
	value_text: '%',
	message: "Percent of peak flow (personal best or predicted)"
});
