data.logic:
var calc_output = [];

//initialize user inputs
var age = parseFloat(age);
var eggs = parseFloat(eggs);

//declare variables
var pBlast, pEuploid;
var msg = ' (assumes 85% survival of thawed eggs)'

// pBlast by age
if (age <= 35) {
	age = 35
	pBlast = 0.95 * Math.exp(2.8043 - 0.1112 * age)
}
else {
	pBlast = 0.85 * Math.exp(2.8043 - 0.1112 * age)
}

// pEuploid values by age
if (age <= 35) {
	pEuploid = 0.574
	msg = ' (assumes 95% survival of thawed eggs)'
}
if (age == 36) {
	pEuploid = 0.564
}
if (age == 37) {
	pEuploid = 0.486
}
if (age == 38) {
	pEuploid = 0.466
}
if (age == 39) {
	pEuploid = 0.440
}
if (age == 40) {
	pEuploid = 0.359
}
if (age == 41) {
	pEuploid = 0.327
}
if (age == 42) {
	pEuploid = 0.285
}
if (age == 43) {
	pEuploid = 0.206
}
if (age == 44) {
	pEuploid = 0.127
}

// var pBirth1 = 1 - Math.pow(1 - 0.6 * pEuploid * pBlast, eggs)
var q = 0.6 * pEuploid * pBlast

// var pBirth1 = 1 - Math.pow(1 - q, eggs)
// var pBirth1 = pBirth1 * 100

// var pBirth2 = pBirth1 - eggs * q * Math.pow(1 - q, eggs - 1)
// var pBirth2 = pBirth2 * 100

// var pBirth3 = pBirth2 - eggs * (eggs - 1) / 2 * Math.pow(q, 2) * Math.pow(1 - q, eggs - 2)
// var pBirth3 = pBirth3 * 100

var pBirth0ex = Math.pow(1 - q, eggs)
var pBirth1ex = eggs * q * Math.pow(1 - q, (eggs - 1))
var pBirth2ex = eggs * (eggs - 1) / 2 * Math.pow(q, 2) * Math.pow(1 - q, (eggs - 2))

var pBirth1 = 1 - pBirth0ex
var pBirth2 = 1 - pBirth0ex - pBirth1ex
var pBirth3 = 1 - pBirth0ex - pBirth1ex - pBirth2ex

var pBirth1 = pBirth1 * 100
var pBirth2 = pBirth2 * 100
var pBirth3 = pBirth3 * 100

if (pBirth1 > 99) {
	pBirth1 = '>99'
}
if (pBirth2 > 99) {
	pBirth2 = '>99'
}
if (pBirth3 > 99) {
	pBirth3 = '>99'
}

if (pBirth1 < 1) {
	pBirth1 = '<1'
}
if (pBirth2 < 1) {
	pBirth2 = '<1'
}
if (pBirth3 < 1) {
	pBirth3 = '<1'
}

if (pBirth1 >= 1 && pBirth1 <= 99) {
	pBirth1 = pBirth1.toFixed(0)
}
if (pBirth2 >= 1 && pBirth2 <= 99) {
	pBirth2 = pBirth2.toFixed(0)
}
if (pBirth3 >= 1 && pBirth3 <= 99) {
	pBirth3 = pBirth3.toFixed(0)
}


calc_output.push({
	name: 'mini',
	value: pBirth1,
	value_text: '%',
	message: 'Probability of at least 1 live birth',
});
calc_output.push({
	name: '3937_BWH Egg Freezing_result (1 birth)',
	value: pBirth1,
	value_text: '%',
	message: 'Probability of at least 1 live birth' + msg,
});
calc_output.push({
	name: '3937_BWH Egg Freezing_result (2 births)',
	value: pBirth2,
	value_text: '%',
	message: 'Probability of at least 2 live births' + msg,
});
calc_output.push({
	name: '3937_BWH Egg Freezing_result (3 births)',
	value: pBirth3,
	value_text: '%',
	message: 'Probability of at least 3 live births' + msg,
});
