data.logic:
var calc_output = [];

var weight = parseFloat(weight)

// need US units for weight
if (UOMSYSTEM == true) {
  weight = parseFloat(weight) / 2.2;
}

if (weight > 100) {
	weight_adj = 100
}
else {
	weight_adj = weight
}

var bolus = weight_adj * 0.09
var infusion = weight_adj * 0.81
var waste = 100 - (bolus + infusion)

calc_output.push({
	name: 'mini',
	value: bolus.toFixed(1),
	value_text: 'mg bolus',
	message: infusion.toFixed(1) + ' mg infusion',
});
calc_output.push({
	name: '3940_tPA Dosing for Stroke Calculator_result (bolus)',
	value: bolus.toFixed(1),
	value_text: 'mg',
	message: 'Bolus dose, given IV over 1 min',
});
calc_output.push({
	name: '3940_tPA Dosing for Stroke Calculator_result (infusion)',
	value: infusion.toFixed(1),
	value_text: 'mg',
	message: 'Infusion, given IV over 60 mins',
});
calc_output.push({
	name: '3940_tPA Dosing for Stroke Calculator_result (waste)',
	value: waste.toFixed(1),
	value_text: 'mg',
	message: 'Waste, to be discarded',
});
