data.logic:
//outputs
var calc_output = [];

//user inputs
var facpal = parseFloat(facpal);
var arm = parseFloat(arm);
var leg = parseFloat(leg);
var gaze = parseFloat(gaze);
var aph = parseFloat(aph);
var agn = parseFloat(agn);
var hemi = parseFloat(hemi);

//calculation
var u;
if (hemi == 0){		//if left hemiparesis, use agn value
	u = facpal + arm + leg + agn + gaze;
}else{					//if right hemiparesis, use aph value
	u = facpal + arm + leg + aph + gaze;
}

//results
var sens, spec, ref
var msg = '';
if (u == 0){
	sens = 'N/A';
	spec = 'N/A';
	ref = 0;
}else if (u == 1){
	sens = '100';
	spec = '13';
	ref = 0;
}else if(u == 2){
	sens = '97';
	spec = '27';
	ref = 0;
}else if(u == 3){
	sens = '93';
	spec = '40';
	ref = 0;
}else if(u == 4){
	sens = '89';
	spec = '55';
	ref = 0;
}else if(u == 5){
	sens = '85';
	spec = '68';
	ref = 1;
}else if(u == 6){
	sens = '72';
	spec = '77';
	ref = 1;
}else if(u == 7){
	sens = '53';
	spec = '89';
	ref = 1;
}else if(u == 8){
	sens = '32';
	spec = '95';
	ref = 1;
}else{
	sens = '7';
	spec = '99';
	ref = 1;
}

var msg1 = 'RACE Scale';
if (ref == 1){
	msg = '. While protocols vary, it is generally recommended that patients with scores ≥5 are directly referred to a stroke center with endovascular capabilities.';
}

var mini_msg;
if (u < 5) {
	mini_msg = 'LVO unlikely'
}
if (u >= 5) {
	mini_msg = 'LVO likely'
}

//result for user
calc_output.push({
	name: 'mini',
	value: u,
	value_text: 'points',
	message: mini_msg
});
calc_output.push({
	name: '3941_RACE Scale_result (score)',
	value: u,
	value_text: 'points',
	message: msg1 + msg
});
calc_output.push({
	name: '3941_RACE Scale_result (sensitivity)',
	value: sens,
	value_text: '%',
	message: 'Sensitivity (for LVO)'
});
calc_output.push({
	name: '3941_RACE Scale_result (specificity)',
	value: spec,
	value_text: '%',
	message: 'Specificity (for LVO)'
});
