data.logic:
var calc_output = [];

//user inputs
var age = parseFloat(age);
var hx = parseFloat(hx);
var wopro = parseFloat(wopro);
var aecg = parseFloat(aecg);

//calculation & interpretation
var u = age + hx + wopro + aecg;
var mor;
if(u == 0){
	mor = '0';
}else if(u == 1){
	mor = '0.8';
}else if(u == 2){
	mor = '19.6';
}else if(u == 3){
	mor = '34.7';
}else if(u == 4){
	mor = '57.1';
}




//results
calc_output.push({
    name: 'mini',
    value: u,
    value_text: 'points',
    message: mor + '% mortality'
});
calc_output.push({
    name: 'Score',
    value: u,
    value_text: 'points',
    message: 'OESIL Score'
});
calc_output.push({
    name: 'Msg',
    value: mor,
    value_text: '%',
    message: '12-month all-cause mortality'
});
