data.logic:
var calc_output = [];

//user inputs - major criteria
var wit = parseFloat(wit);
var abn = parseFloat(abn);
var hx = parseFloat(hx);
var vomit = parseFloat(vomit);
var sus = parseFloat(sus);
var seiz = parseFloat(seiz);
var gcs = parseFloat(gcs);
var sign = parseFloat(sign);
var pos = parseFloat(pos);
var pres = parseFloat(pres);
var road = parseFloat(road);
var fall = parseFloat(fall);
var high = parseFloat(high);
var nai = parseFloat(nai);

//interpretation
var u = wit + vomit + sus + seiz + gcs + sign + pos + pres + road + fall + high + abn + hx + nai;
var msg, ct, sens;
if (u == 0){
	msg = "Low";
    ct = 'CT not necessary.'
    sens = '98% sensitive (death, neurosurgical intervention, abnormal CT).'
}else{
	msg = "Not low";
	ct = 'Consider CT.';
    sens =''
}


//results
calc_output.push({
    name: 'mini',
    value: msg,
    value_text: 'risk',
    message: ct
});
calc_output.push({
    name: 'Score',
    value: msg,
    value_text: 'risk',
    message: ct + '<br/>' + sens
});
