data.logic:
var calc_output = [];

//user inputs
var ams = parseFloat(ams) ? 1 : 0;          //A
var skull = parseFloat(skull) ? 1 : 0;      //B
var hx = parseFloat(hx) ? 1 : 0;            //C
var scalp = parseFloat(scalp) ? 1 : 0;      //D
var ache = parseFloat(ache) ? 1 : 0;        //E

var u = ams + skull + hx + scalp + ache;

//interpretation 
var risk = 'Not low';
var ct, intervention;
if(ams == 1){
    ct = '15.0';
    intervention = '16.3';
}
if(skull == 1){
    ct = '13.3';
    intervention = '11.8';
} 
if(hx == 1){
    ct = '4.5';
    intervention = '3.2';
}
if(scalp == 1){
    ct = '10.5';
    intervention = '0';
}
if(ache == 1){
    ct = '0.5';
    intervention = '0.6';
}
if(u == 0){
    ct = '0.3';
    intervention = '0';
    risk = 'Low';
    msg = 'CT not necessary'
}
if(u > 0){
    risk = 'Not low';
    msg = 'Consider CT'
}

//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: 'risk',
    message: msg
});
calc_output.push({
    name: 'UCDavis',
    value: risk,
    value_text: 'risk',
    message: msg
});
calc_output.push({
    name: 'TBIintervention',
    value: intervention,
    value_text: '%',
    message: 'TBI requiring intervention'
});
calc_output.push({
    name: 'TBICT',
    value: ct,
    value_text: '%',
    message: 'TBI seen on CT'
});
