data.logic:
var calc_output = [];

//user inputs
var age = parseFloat(age);
var sex = parseFloat(sex);
var type = parseFloat(type);
//recent
var major = parseFloat(major);
var chf = parseFloat(chf);
var sepsis = parseFloat(sepsis);
var pneu = parseFloat(pneu);
var preg = parseFloat(preg);
var plaster = parseFloat(plaster);
var fx = parseFloat(fx);
var stroke = parseFloat(stroke);
var multi = parseFloat(multi);
var acute = parseFloat(acute);
//venous
var vari = parseFloat(vari);
var curr = parseFloat(curr);
var cent = parseFloat(cent);
var dvt = parseFloat(dvt);
var fam = parseFloat(fam);
var factv = parseFloat(factv);
var pospro = parseFloat(pospro);
var serum = parseFloat(serum);
var lupus = parseFloat(lupus);
var elev = parseFloat(elev);
var hep = parseFloat(hep);
var other = parseFloat(other);
//other hx
var hxbowel = parseFloat(hxbowel);
var bmi = parseFloat(bmi);
var acutemi = parseFloat(acutemi);
var copd = parseFloat(copd);
var riskfacts = parseFloat(riskfacts);
var oralconp = parseFloat(oralconp);
var hx = parseFloat(hx);
var mobility = parseFloat(mobility);
var cancer = parseFloat(cancer);

if (sex == 0) {
	oralconp = 0
	preg = 0
	hx = 0
}
else {
	oralconp = oralconp
	preg = preg
	hx = hx
}

//score, add up the points
var u = age+type+major+chf+sepsis+pneu+preg+plaster+fx+stroke+multi+acute+vari+curr+cent+dvt+fam+factv+pospro+serum+lupus+elev+hep+other+hxbowel+bmi+acutemi+copd+riskfacts+oralconp+hx+mobility+cancer;

//interpretation
var cate, percent, rec, duration;

if(u == 0){
	cate = 'Lowest';
	percent = 'Minimal';
	rec = 'Early frequent ambulation only, OR at discretion of surgical team: pneumatic compression devices OR graduated compression stockings';
	duration = 'During hospitalization';
}
if(u==1 || u==2){
	cate = 'Low';
	percent = 'Minimal';
	rec = 'Pneumatic compression devices ± graduated compression stockings';
	duration = 'During hospitalization';
}
if(u==3 || u==4){
	cate = 'Moderate';
	percent = '0.7%';
	rec = 'Pneumatic compression devices ± graduated compression stockings';
	duration = 'During hospitalization';
}

if(u==5 || u==6){
	cate = 'High';
	percent = '1.8%';
	rec = 'Pneumatic compression devices AND low dose heparin OR low molecular weight heparin';
	duration = '7-10 days total';
}
if(u==7 || u==8){
	cate = 'High';
	percent = '4.0%';
	rec = 'Pneumatic compression devices AND low dose heparin OR low molecular weight heparin';
	duration = '7-10 days total';
}
if( u>= 9){
	cate = 'Highest';
	percent = '10.7%';
	rec = 'Pneumatic compression devices AND low dose heparin OR low molecular weight heparin';
	duration = '30 days total';
}


//results
calc_output.push({
    name: 'mini',
    value: cate,
    value_text: 'risk',
    message: rec
});
calc_output.push({
    name: '3970_Caprini Score_result',
    value: u,
    value_text: 'points',
    message: 'Caprini VTE Score'
});
calc_output.push({
    name: '3970_Caprini Score_interpretation (risk)',
    value: cate,
    value_text: 'risk',
    message: 'Recommended prophylaxis: '+rec
});
calc_output.push({
    name: '3970_Caprini Score_interpretation (VTE risk)',
    value: percent,
    value_text: 'VTE risk',
    message: duration + ' for duration of chemoprophylaxis'
});
