data.logic:
var calc_output = [];

//user inputs
var evidence = parseFloat(evidence);
var gcs = parseFloat(gcs);
var abd = parseFloat(abd);
var thora = parseFloat(thora);

//add up the points
//var u = evidence + gcs + abd + thora + co + breath + vomit;

//interpretation
var risk, percent, msg;

if (evidence == 1 || gcs == 1) {
	risk = 'Not very low';
	percent = 5.4;
    msg = 'Proceed with standard workup, including CT abdomen as indicated.'
} else if (abd == 1) {
    risk = 'Not very low';
    percent = 1.4;
    msg = 'Proceed with standard workup, including CT abdomen as indicated.'; 
} else if (thora == 1) {
	risk = 'Not very low';
	percent = '0.7';
    msg = 'Proceed with standard workup, including CT abdomen as indicated.';
} else {
    risk = 'Very low';
	percent = '0.1';
    msg = 'Consider deferring CT abdomen.';
}

//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: ' risk',
    message: percent + '% risk of intra-abdominal injury requiring intervention'
});
calc_output.push({
    name: '3971_PECARN IAI_result',
    value: percent,
    value_text: '%',
    message: 'Risk of intra-abdominal injury requiring intervention'
});
calc_output.push({
    name: '3971_PECARN IAI_interpretation',
    value: risk,
    value_text: 'risk',
    message: msg
});
