data.logic:
var calc_output = [];



//addition of points
var v = parseInt(history) + parseInt(ekg) + parseInt(age) + parseInt(risk_factors) + parseInt(troponin);


//interpretation
var risk, risk2, rec;
if (v < 4) { 
	if(troponin == 0){
		risk = 'Low';
		risk2 = '0.9-1.7% 30-day MACE';
    	rec = 'Repeat troponin at 3 hours and if negative, discharge home with outpatient follow-up.'; 
    }else if(0 < troponin < 3){
    	risk = 'High';
    	risk2 = '12-65% 30-day MACE';
    	rec = 'Cardiology consultation and admission recommended. Further testing indicated.';
    }

}
if (v > 3) { 
	risk = 'High';
    risk2 = '12-65% 30-day MACE';
    if(troponin == 0){
    	rec = 'Admit to hospital or observation. Further testing indicated.'; 
    }else if(0 < troponin < 3){
    	rec = 'Cardiology consultation and admission recommended. Further testing indicated.';
    }
}


calc_output.push({ 
	name: 'mini', 
	value: risk, 
	value_text: 'risk', 
	message: risk2
});
calc_output.push({ 
	name: 'HEART Score', 
	value: v, 
	value_text: 'points', 
	message: 'HEART Pathway Score'
});
calc_output.push({ 
	name: 'Risk', 
	value: risk, 
	value_text: 'risk', 
	message: risk2 + '<br/><br/>' + rec
});
